import { useState, useEffect } from 'react';

const useFetchApi = url => {

    const [resource, setResource] = useState([]);

    useEffect(() => {
        const getResource = async () => {
          const data = await fetch(url);
          const response = await data.json();

        }
        getResource();   
    },[]);

    return resource;

}

export default useFetchApi;
