import React,{useEffect,useState} from 'react';

const CountryList = () => {

  const [country,setCountry] = useState([]);

  const getCountries = async () => {

    const data = await fetch(`http://127.0.0.1:8000/api/countries/list`);
    const countries = await data.json();
    setCountry(countries); 
  }

  const deleteContries = async (id) => {
    const data = await fetch(`http://127.0.0.1:8000/api/countries/delete-country/${id}`);
    const response = await data.json();
    response.success === true ? alert(response.message) : alert('Ha ocurrido un error.');
    getCountries();
  }

  useEffect(() => {
    getCountries();
  },[]);

  const [form,setForm] = useState({
    countryName : '',
    countryPrefix : '',
    countryDomain : '',
    countryFlag : ''
  }); 

  const handleInputChange = (e) => {
    setForm({
      ...form,
      [e.target.name] : e.target.value
    });
  };


  const insertCountry = async (e) => {
    e.preventDefault();
    const data = await fetch('http://127.0.0.1:8000/api/countries/create-country',{
      method: 'POST',
      body: JSON.stringify(form),
      headers: { 'Content-Type': 'application/json' },
    });
    const response = await data.json();
    response.success === true ? alert(response.message) : alert('Ha ocurrido un error.');
  };

  return (
  <div className='table-container'>
    <table>
      <thead>
        <tr>
          <th>ID</th>
          <th>Nombre País</th>
          <th>Prefijo País</th>
          <th>Dominio País</th>
          <th>Bandera País</th>
          <th>Acciones</th>
        </tr>
      </thead>
      <tbody>
        {
          country.map( item => (
            <tr key={item.id}>
              <td>{item.id}</td>
              <td>{item.country_name}</td>
              <td>{item.country_prefix}</td>
              <td>{item.country_domain}</td>
              <td>{item.country_flag}</td>
              <td>
                <button> Modificar </button>
                <button onClick={(e) => deleteContries(item.id, e)} >Eliminar</button>
              </td>
            </tr>
          ))
        }
      </tbody>
    </table>
    <div>
      <form onSubmit={insertCountry}>
        <div>
          <input 
            type="text"
            name="countryName" 
            onChange={handleInputChange}
            placeholder="Nombre del País"
          />
        </div>
        <div>
          <input 
            type="text"
            name="countryPrefix" 
            onChange={handleInputChange}
            placeholder="Prefijo del País" 
          />
        </div>
        <div>
          <input 
            type="text"
            name="countryDomain" 
            onChange={handleInputChange}
            placeholder="Dominio del País" 
          />
        </div>
        <div>
          <input 
            type="text"
            name="countryFlag" 
            onChange={handleInputChange}
            placeholder="Bandera del País" 
          />
        </div>
        <div>
          <button type="submit">Añadir País</button>
        </div>
      </form>
    </div>
  </div>

  )

}


export default CountryList;
